mapper.update:
UPDATE contents 
SET title=#{title}, content=#{content}, web=#{web}, word=#{word}
WHERE contentsno=#{contentsno}

mapper.select:
SELECT COUNT(*) as passwd_cnt
FROM contents
WHERE contentsno=#{contentsno} AND passwd=#{passwd}

mapper.update:
UPDATE contents
SET file1=#{file1}, thumb1=#{thumb1}, size1=#{size1}
WHERE contentsno=#{contentsno}
